import { useContext } from "react";
import { dataContext } from "../Context/DataContext";

import "./Productos.css";

const Productos = () => {
  const { data, cart, setCart } = useContext(dataContext);

  const buyProducts = (product) => {
    const productor = cart.find((item) => item.id === product.id);

    if(productor){
      setCart(cart.map((item) => (item.id === product.id ? {...product, quanty: productor.quanty + 1} : item)));
    }else{
      setCart([...cart, product]);
    }
  };

  return data.map((product)=> {
    return(
        <div className="card" key={product.id}>
            <img src={product.img} alt="img-pronduct-card" />
            <h3>{product.name}</h3>
            <h4>$ {product.price} MXN</h4>
            <button onClick={() => buyProducts(product)}>Agregar al carrito</button>
        </div>
    );
  });
};

export default Productos
